        const id = img.match(/\/d\/(.+?)\//);
        if (id) img = `https://lh3.googleusercontent.com/d/${id[1]}`;
    }

    let price = Number(String(item['Үнэ /сар/'] || item['Үнэ'] || "0").replace(/[^0-9.]/g, ''));

    let cat = item['Ангилал'] || item[' Ангилал '] || "Unknown";

    let floor = item['Давхар'] || "";

    return { 
        id: code, 
        location: item['Байршил'], 
        category: cat, 
        size: item['Хэмжээ'],
        floor: floor,
        price: price, 
        image: img

    };
}).filter(r => r !== null);

