/* 2. Safety Check */
if (!items || items.length === 0) return "₮ 0";

/* 3. Calculate Total (Price * Duration) */
const total = items.reduce((sum, item) => {
    // Ensure price is a clean number
    const price = Number(item.price);
    // Use duration if it exists, otherwise default to 1 month
    const months = item.duration || 1;
    
    return sum + (price * months);
}, 0);

/* 4. Return Formatted Money */
return "₮ " + total.toLocaleString();

